"use strict";if(document.querySelector(".slider")){new Swiper(".swiper-container",{loop:!0,navigation:{nextEl:".swiper-next",prevEl:".swiper-prev",autoplay:{delay:2500}}})}const links=document.querySelectorAll(".item-list__link[data-goto]");function onMenuLinkClick(e){const t=e.target;if(t.dataset.goto&&document.querySelector(t.dataset.goto)){e.preventDefault();const o=document.querySelector(t.dataset.goto).getBoundingClientRect().top+window.pageYOffset-document.querySelector("header").offsetHeight;window.scrollTo({top:o,behavior:"smooth"})}}links.length>0&&links.forEach((e=>{e.addEventListener("click",onMenuLinkClick)}));